class ResponseKoleksiBook {
  ResponseKoleksiBook({
      this.status, 
      this.message, 
      this.data,});

  ResponseKoleksiBook.fromJson(dynamic json) {
    status = json['Status'];
    message = json['Message'];
    if (json['data'] != null) {
      data = [];
      json['data'].forEach((v) {
        data?.add(DataBookmark.fromJson(v));
      });
    }
  }
  int? status;
  String? message;
  List<DataBookmark>? data;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['Status'] = status;
    map['Message'] = message;
    if (data != null) {
      map['data'] = data?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

